import React from 'react';
import { Carousel } from 'react-bootstrap';

const AnimalSlider = () => {
  return (
    <Carousel>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://images.yourstory.com/cs/wordpress/2015/07/yourstory-bengal-tiger.jpg"
          alt="First slide"
          style={{ maxHeight: '400px', objectFit: 'cover' }} // Set max height and object-fit
        />
        <Carousel.Caption className="text-center" style={{ bottom: '20px', left: '0', right: '0', margin: 'auto' }}>
          {/* Adjust bottom value and margin to center the caption */}
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      {/* Add more Carousel.Item components for additional slides */}
    </Carousel>
  );
};

export default AnimalSlider;
