import React from 'react';

console.clear();

const slides = [
  {
    name: "Harambe",
    species: "Gorilla gorilla gorilla (Western Lowland Gorilla)",
    sex: "Male",
    age: "17",
    image: "https://unsplash.com/photos/a-gorilla-sitting-on-top-of-a-rock-next-to-a-waterfall-v7HgfgFsOb4"
  },
  {
    name: "John Porkington",
    species: "Sus Domesticus (Domestic Pig)",
    sex: "Male",
    age: "5",
    image: "https://unsplash.com/photos/pig-lies-on-ground-CP9GGy_LkIY"
  },
  {
    name: "Frederick Leopold Fazbearington",
    species: "Ursus Arctos Horribilis (Grizzly Bear)",
    sex: "Male",
    age: "21",
    image: "https://unsplash.com/photos/brown-bear-sitting-on-grass-field-y421kXlUOQk",
  }

];

const breakPoints = [
  { width: 1, itemsToShow: 1},
  { width: 550, itemsToShow: 2},
  { width: 768, itemsToShow: 3},
  { width: 1200, itemsToShow: 4},
];

const init = {
  slideIndex: slides[0]
}

const slideReducer = (state, event) => {
  if (event.type === 'NEXT') {
    return {
      ...state,
      slideIndex: (state.slideIndex + 1) % slides.length
    }
  }
  if (event.type === 'PREV') {
    return {
      ...state,
      slideIndex: state.slideIndex === 0 ? slides.length - 1: state.slideIndex - 1
    }
  }
}

function HomePage() {
  const [state,dispatch] = React.useReducer(slideReducer, init);
  return (
    <div>
      <h4>Homepage</h4>
      <h1 style={{ textAlign: "center" }}>Big Cats Rescue</h1>
      <h2 style={{ textAlign: "center" }}>Meet Our Most Popular Animals</h2>
      {slides.map( slide=> {
        return <div className="slide">{slide.name}</div>
      })}
      <button onClick={() => dispatch('PREV') }>Prev</button>
      <button onClick={() => dispatch('NEXT') }>Next</button>
    </div>
  );
}

export default HomePage;
