import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios'; // Assuming you're using Axios for API calls

const Gallery = () => {
  const [animals, setAnimals] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8000/animals'); // Replace with your actual backend API endpoint
        setAnimals(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container">
      <h1>Animal Gallery</h1>
      <Row xs={1} md={2} lg={3} xl={4}>  {/* Adjust grid layout as needed */}
        {animals.map((animal) => (
          <Col key={animal.id}>
            <Card>
              {animal.image && ( // Check for image existence before rendering
                <Card.Img variant="top" src={animal.image} alt={animal.name} />
              )}
              <Card.Body>
                <Card.Title>{animal.name}</Card.Title>
                <Card.Text>
                  <p>Age: {animal.age}</p>
                  <p>Sex: {animal.sex}</p>
                  <p>{animal.description}</p>
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Gallery;
