import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

function Gallery() {
  const [animals, setAnimals] = useState([]);

  useEffect(() => {
    const fetchAllAnimals = async () => {
      try {
        const res = await axios.get("http://localhost:8000/animals");
        setAnimals(res.data); // Assuming res.data contains an array of animal objects
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllAnimals();
  }, []);

  return (
    <div>
      <h1>Gallery</h1>
      <div className="card-container">
      {animals.map(animal => (
  <Card key={animal.id} style={{ width: '18rem' }}>
    <Card.Img variant="top" src={animal.image} alt={animal.name} />
    <Card.Body>
      <Card.Title>{animal.name}</Card.Title>
      <Card.Text>
        <p>Age: {animal.age}</p>
        <p>Sex: {animal.sex}</p>
        <p>Description: {animal.description}</p>
      </Card.Text>
      <Button variant="primary">Learn More</Button>
    </Card.Body>
  </Card>
))}

      </div>
    </div>
  );
}

export default Gallery;
